parameters.functionCode:
// Hash personal data for Facebook API
const crypto = require('crypto');

function hashSha256(value) {
  if (!value) return '';
  const normalized = value.toString().trim().toLowerCase();
  return crypto.createHash('sha256').update(normalized).digest('hex');
}

// Extract and hash user data
const userData = {
  em: [hashSha256($json.email)],
  ph: $json.phone ? [hashSha256($json.phone.replace(/[^\d]/g, ''))] : [],
  fn: $json.first_name ? [hashSha256($json.first_name)] : [],
  ln: $json.last_name ? [hashSha256($json.last_name)] : []
};

// Prepare custom data with UTM parameters and conversion value
const customData = {
  currency: 'USD',
  value: parseFloat($json.deal_value) || 100, // Use actual deal value or default
  lead_id: $json.lead_id,
  pipeline_stage: 'Lead Ganho',
  event_type: 'conversion_completed',
  deal_value: $json.deal_value,
  deal_id: $json.deal_id || $json.lead_id
};

// Add deal-specific data
if ($json.deal_value) customData.revenue = parseFloat($json.deal_value);
if ($json.deal_type) customData.deal_type = $json.deal_type;
if ($json.property_type) customData.property_type = $json.property_type;
if ($json.closing_date) customData.closing_date = $json.closing_date;
if ($json.commission) customData.commission = parseFloat($json.commission);

// Add UTM data if available
if ($json.utm_source) customData.utm_source = $json.utm_source;
if ($json.utm_medium) customData.utm_medium = $json.utm_medium;
if ($json.utm_campaign) customData.utm_campaign = $json.utm_campaign;
if ($json.utm_content) customData.utm_content = $json.utm_content;
if ($json.utm_term) customData.utm_term = $json.utm_term;

// Prepare Facebook event payload for conversion
const facebookEvent = {
  event_name: 'Purchase',
  event_time: Math.floor(Date.now() / 1000),
  event_source_url: $json.source_url || 'https://yoursite.com',
  user_data: userData,
  custom_data: customData,
  action_source: 'other'
};

console.log(`[LEAD GANHO EVENT] Prepared Facebook conversion event for lead ${$json.lead_id} with value ${customData.value}`);

return {
  facebook_event: facebookEvent,
  original_data: $json,
  processed_at: new Date().toISOString()
};

// Log successful Facebook conversion event
const logEntry = {
  timestamp: new Date().toISOString(),
  level: 'SUCCESS',
  event_type: 'lead_ganho',
  lead_id: $json.original_data.lead_id,
  facebook_response: {
    events_received: $json.events_received || 0,
    events_dropped: $json.events_dropped || 0,
    messages: $json.messages || []
  },
  event_data: {
    event_name: 'Purchase',
    user_hashed: true,
    utm_included: !!$json.original_data.utm_source,
    conversion_data: {
      deal_value: $json.original_data.deal_value,
      deal_type: $json.original_data.deal_type,
      property_type: $json.original_data.property_type,
      commission: $json.original_data.commission
    }
  },
  message: 'Lead Ganho conversion event successfully sent to Facebook'
};

console.log(`[FACEBOOK LEAD GANHO SUCCESS] Lead ${$json.original_data.lead_id} - Conversion event sent with value ${$json.original_data.deal_value}`);

return logEntry;

