// Log Facebook API error
const error = $json.error || $input.all()[0].json.error || 'Unknown Facebook API error';
const leadId = $input.all()[0].json.original_data?.lead_id || $input.all()[0].json.lead_id || 'unknown';

const errorLog = {
  timestamp: new Date().toISOString(),
  level: 'ERROR',
  event_type: 'visita',
  lead_id: leadId,
  error_type: 'facebook_api_failed',
  error_message: error.toString(),
  error_details: $json,
  message: 'Failed to send Visita event to Facebook'
};

console.error(`[FACEBOOK VISITA ERROR] Lead ${leadId} - Error: ${error}`);

// Trigger alert for critical Facebook API failures
if (error.toString().includes('Invalid OAuth') || error.toString().includes('Rate limit')) {
  console.error(`[CRITICAL ALERT] Facebook API issue: ${error}`);
}

return errorLog;

